import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";
import RecipeCard from "./RecipeCard"; // Ensure you have this component for displaying recipes
import { Link } from "react-router-dom"; // Import Link for navigation

export default function PreviousSearches() {
    const [searchTerm, setSearchTerm] = useState('');
    const [recipes, setRecipes] = useState([]);

    const searches = ['pizza', 'burger', 'cookies', 'juice', 'biryani', 'salad', 'lasagna', 'pudding', 'ice-cream', 'soup'];

    const handleSearch = async () => {
        const response = await fetch(`http://localhost:3000/recipes?search=${encodeURIComponent(searchTerm)}`);
        const data = await response.json();
        setRecipes(data);
    };

    return (
        <div className="previous-searches section">
            <h2>Previous Searches</h2>

            {/* Create Recipe Button */}
            <Link to="/create-recipe" className="btn create-recipe-btn">Create Recipe</Link>

            <div className="previous-searches-container">
                {searches.map((search, index) => (
                    <div key={index} style={{ animationDelay: index * 0.1 + "s" }} className="search-item">
                        {search}
                    </div>
                ))}
            </div>

            <div className="search-box">
                <input 
                    type="text" 
                    placeholder="SEARCH FOR RECIPE" 
                    value={searchTerm} 
                    onChange={(e) => setSearchTerm(e.target.value)} 
                />
                <button className="btn" onClick={handleSearch}>
                    <FontAwesomeIcon icon={faSearch} />
                </button>
            </div>

            <div className="recipes-container">
                {recipes.length > 0 ? (
                    recipes.map((recipe) => (
                        <RecipeCard key={recipe._id} recipe={recipe} />
                    ))
                ) : (
                    <p>No recipes found.</p>
                )}
            </div>
        </div>
    );
}
