import React, { useState } from 'react';
import CreateRecipe from '../components/CreateRecipe'; // Import the CreateRecipe component

export default function ImproveSkills() {
    const [isOpen, setIsOpen] = useState(false); // State to control modal visibility
    const list = [
        "Learn New Recipes",
        "Experiment with Food",
        "Write your own recipes",
        "Know Nutritional Facts",
        "Get Cooking Tips",
        "Get Ranked"
    ];

    const toggleModal = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className="section improve-skills">
            <div className="col img">
                <img id="myImage" src="/img/gallery/img_10.jpg" alt="Culinary Skills" />
            </div>
            <div className="col typography">
                <h1 className="title">Let's Build our Culinary Skills</h1>
                {list.map((item, index) => (
                    <p className="skill-item" key={index}>{item}</p>
                ))}
                <button className="btn" onClick={toggleModal}>Create Recipe</button> {/* Create Recipe button */}
                {isOpen && <CreateRecipe />} {/* Show CreateRecipe component as modal */}
            </div>
        </div>
    );
}
