import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import CustomImage from '../components/CustomImage'; // Import the CustomImage component

export default function RecipeDetail() {
    const { id } = useParams(); // Get the recipe ID from the URL
    const [recipe, setRecipe] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchRecipe = async () => {
        try {
            const response = await fetch(`http://localhost:3000/recipes/${id}`);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setRecipe(data);
        } catch (error) {
            console.error('Error fetching recipe:', error);
            setError('Failed to load recipe. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchRecipe();
    }, [id]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;
    if (!recipe) return <p>No recipe found.</p>;

    return (
        <div className="recipe-detail">
            <h1>{recipe.name}</h1>
            <div className="image-container">
                <CustomImage 
                    imgSrc={recipe.mainImage ? `http://localhost:3000/${recipe.mainImage}` : 'default-image-url.jpg'} 
                    pt="65%" 
                />
            </div>
            <h2>Ingredients:</h2>
            <ul>
                {recipe.ingredients.split(',').map((ingredient, index) => (
                    <li key={index}>{ingredient.trim()}</li>
                ))}
            </ul>
            <h2>Preparation Level:</h2>
            <p>{recipe.preparationLevel || 'Not specified'}</p>
            <h2>Steps:</h2>
            <p>{recipe.steps || 'No steps provided.'}</p>
            <h2>Description:</h2>
            <p>{recipe.description || 'No description available.'}</p>
        </div>
    );
}
