import React, { useEffect, useState } from 'react';
import PreviousSearches from "../components/PreviousSearches";
import RecipeCard from "../components/RecipeCard";
import CreateRecipe from '../components/CreateRecipe'; // Adjust the path as necessary
import SearchComponent from '../components/SearchComponent'; // Import the SearchComponent

export default function Recipes() {
    const [recipes, setRecipes] = useState([]); // State to hold recipes
    const [loading, setLoading] = useState(true); // State to manage loading state
    const [error, setError] = useState(null); // State to manage error
    const [searchResults, setSearchResults] = useState([]); // State to hold search results

    // Fetch recipes from the server
    const fetchRecipes = async () => {
        setLoading(true); // Start loading
        try {
            const response = await fetch('http://localhost:3000/recipes'); // Adjust the URL if needed
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setRecipes(data); // Set fetched recipes
            setError(null); // Reset error on successful fetch
        } catch (error) {
            console.error('Error fetching recipes:', error);
            setError('Failed to load recipes.'); // Set error message
        } finally {
            setLoading(false); // End loading
        }
    };

    useEffect(() => {
        fetchRecipes(); // Call fetchRecipes when the component mounts
    }, []);

    const handleSearchResults = (results) => {
        setSearchResults(results); // Update the search results state
    };

    return (
        <div>
            <div className="search-section">
                <PreviousSearches 
                    searches={['pizza', 'burger', 'cookies', 'juice', 'biryani', 'salad', 'lasagna', 'pudding', 'ice-cream', 'soup']} 
                    onSearchClick={handleSearchResults} 
                />
                <SearchComponent onResults={handleSearchResults} />
            </div>
            <div className="recipes-tab">
                <CreateRecipe />
            </div>
            <div className="recipes-container">
                {loading ? (
                    <p>Loading recipes...</p> // Loading message
                ) : error ? (
                    <p>{error}</p> // Display error message if any
                ) : (
                    <>
                        {searchResults.length > 0 && <h2>Search Results:</h2>}
                        {(searchResults.length > 0 ? searchResults : recipes).map((recipe) => (
                            <RecipeCard key={recipe._id} recipe={recipe} /> // Use _id for unique key
                        ))}
                    </>
                )}
            </div>
        </div>
    );
}
