import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

const SearchComponent = ({ onResults }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [hasSearched, setHasSearched] = useState(false);
    const [results, setResults] = useState([]);

    const handleInputChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleSearchSubmit = async () => {
        await searchRecipes(searchTerm);
        setHasSearched(true);
    };

    const searchRecipes = async (query) => {
        if (!query) return;
        try {
            const response = await fetch(`http://localhost:3000/recipes?search=${query}`);
            if (response.ok) {
                const data = await response.json();
                setResults(data);
                onResults(data); // Pass results back to parent if needed
            } else {
                console.error('Failed to fetch recipes:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching recipes:', error);
        }
    };

    return (
        <div className="search-box">
            <input
                type="text"
                value={searchTerm}
                onChange={handleInputChange}
                placeholder="SEARCH FOR RECIPE"
            />
            <button className="btn" onClick={handleSearchSubmit}>
                <FontAwesomeIcon icon={faSearch} />
            </button>
            {hasSearched && results.length === 0 && <p>No recipes found.</p>}
            <div className="search-results">
                {results.map((recipe) => (
                    <div key={recipe._id} className="recipe-item">
                        <h3>{recipe.name}</h3>
                        <p>{recipe.description}</p>
                        {recipe.mainImage && <img src={`uploads/${recipe.mainImage}`} alt={recipe.name} />}
                        <Link to={`/recipes/${recipe._id}`} className="view-btn">View Recipe</Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default SearchComponent;
