import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';

const RecipeCard = ({ recipe }) => {
    return (
        <div className="recipe-card">
            <Link to={`/recipes/${recipe._id}`} className="recipe-link">
                <img 
                    src={recipe.mainImage ? `http://localhost:5000/${recipe.mainImage}` : 'default-image-url.jpg'} 
                    alt={recipe.name} 
                    className="custom-image"
                />
                <div className="recipe-card-info">
                    <h2 className="recipe-title">{recipe.name}</h2>
                    <p className="recipe-desc">
                        {recipe.description || 'No description available.'}
                    </p>
                    <span className="view-btn">View Recipe</span>
                </div>
                {/* Optional author image section if needed */}
                {/* <img src={authorImage} alt="Author" className="author-img" /> */}
            </Link>
        </div>
    );
};

RecipeCard.propTypes = {
    recipe: PropTypes.shape({
        _id: PropTypes.string.isRequired,
        name: PropTypes.string.isRequired,
        mainImage: PropTypes.string,
        description: PropTypes.string,
    }).isRequired,
};

export default RecipeCard;
