import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom'; // Import Link

export default function PreviousSearches() {
    const searches = ['pizza', 'burger', 'cookies', 'juice', 'biryani', 'salad', 'lasagna', 'pudding', 'ice-cream', 'soup'];
    const [searchTerm, setSearchTerm] = useState('');
    const [results, setResults] = useState([]);
    const [hasSearched, setHasSearched] = useState(false); // Track if a search has been performed

    const handleSearchClick = (search) => {
        setSearchTerm(search);
        searchRecipes(search);
    };

    const handleInputChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleSearchSubmit = async () => {
        await searchRecipes(searchTerm);
        setHasSearched(true); // Indicate that a search has been performed
    };

    const searchRecipes = async (query) => {
        if (!query) return;
        try {
            const response = await fetch(`http://localhost:3000/recipes?search=${encodeURIComponent(query)}`);
            if (response.ok) {
                const data = await response.json();
                setResults(data);
            } else {
                console.error('Failed to fetch recipes:', response.statusText);
            }
        } catch (error) {
            console.error('Error fetching recipes:', error);
        }
    };

    return (
        <div className="previous-searches section">
            <h2>Previous Searches</h2>
            <div className="previous-searches-container">
                {searches.map((search, index) => (
                    <div
                        key={index}
                        style={{ animationDelay: index * 0.1 + "s" }}
                        className="search-item"
                        onClick={() => handleSearchClick(search)}
                    >
                        {search}
                    </div>
                ))}
            </div>
            <div className="search-box">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleInputChange}
                    placeholder="SEARCH FOR RECIPE"
                />
                <button className="btn" onClick={handleSearchSubmit}>
                    <FontAwesomeIcon icon={faSearch} />
                </button>
            </div>
            {hasSearched && results.length === 0 && <p>No recipes found.</p>} {/* Only show if searched */}
            <div className="search-results">
                {results.map((recipe) => (
                    <div key={recipe._id} className="recipe-item">
                        <h3>{recipe.name}</h3>
                        <p>{recipe.description}</p>
                        <Link to={`/recipes/${recipe._id}`} className="view-btn">View Recipe</Link>
                    </div>
                ))}
            </div>
        </div>
    );
}
