import React, { useEffect, useState } from 'react';
import RecipeCard from './RecipeCard'; // Ensure you have your RecipeCard component imported

export default function Cuisines() {
    const [cuisines, setCuisines] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchCuisines = async () => {
        try {
            const response = await fetch('http://localhost:3000/recipes'); // Adjust this API endpoint if necessary
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            const categorized = data.reduce((acc, recipe) => {
                const cuisine = recipe.cuisine || 'Unknown';
                if (!acc[cuisine]) acc[cuisine] = [];
                acc[cuisine].push(recipe);
                return acc;
            }, {});
            setCuisines(Object.entries(categorized));
        } catch (error) {
            console.error('Error fetching cuisines:', error);
            setError('Failed to load cuisines. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchCuisines();
    }, []);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="cuisines">
            <h1>Recipes by Cuisine</h1>
            {cuisines.map(([cuisine, recipes]) => (
                <div key={cuisine} className="cuisine-category">
                    <h2>{cuisine}</h2>
                    <div className="recipe-cards">
                        {recipes.map(recipe => (
                            <RecipeCard key={recipe._id} recipe={recipe} />
                        ))}
                    </div>
                </div>
            ))}
        </div>
    );
}
