import React, { useEffect, useState } from 'react';
import PreviousSearches from "../components/PreviousSearches";
import RecipeCard from "../components/RecipeCard";
import CreateRecipe from '../components/CreateRecipe'; 
import SearchComponent from '../components/SearchComponent'; 

export default function Recipes() {
    const [recipes, setRecipes] = useState([]); 
    const [loading, setLoading] = useState(true); 
    const [error, setError] = useState(null); 
    const [searchResults, setSearchResults] = useState([]); 
    const [previousSearches, setPreviousSearches] = useState(['pizza', 'burger', 'cookies', 'juice', 'biryani', 'salad', 'lasagna', 'pudding', 'ice-cream', 'soup']); 

    const fetchRecipes = async () => {
        setLoading(true); 
        try {
            const response = await fetch('http://localhost:3000/recipes'); 
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setRecipes(data); 
            setError(null); 
        } catch (error) {
            console.error('Error fetching recipes:', error);
            setError('Failed to load recipes.'); 
        } finally {
            setLoading(false); 
        }
    };

    useEffect(() => {
        fetchRecipes(); 
    }, []);

    const handleSearchResults = (results) => {
        setSearchResults(results); 
    };

    const updatePreviousSearches = (newSearch) => {
        if (!previousSearches.includes(newSearch)) {
            setPreviousSearches((prev) => [...prev, newSearch]); 
        }
    };

    return (
        <div>
            <div className="recipes-tab">
                <CreateRecipe />
            </div>
            <div className="search-section">
                <PreviousSearches 
                    searches={previousSearches} 
                    onSearchClick={handleSearchResults} 
                />
                <SearchComponent onResults={handleSearchResults} onNewSearch={updatePreviousSearches} />
            </div>
            <div className="recipes-container">
                {loading ? (
                    <p>Loading recipes...</p>
                ) : error ? (
                    <p>{error}</p>
                ) : (
                    <>
                        {searchResults.length > 0 && <h2>Search Results:</h2>}
                        {(searchResults.length > 0 ? searchResults : recipes).map((recipe) => (
                            <RecipeCard key={recipe._id} recipe={recipe} />
                        ))}
                    </>
                )}
            </div>
        </div>
    );
}
