import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';

const SearchComponent = ({ onResults, onNewSearch }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [ingredientTerm, setIngredientTerm] = useState('');
    const [difficulty, setDifficulty] = useState('');

    const handleInputChange = (e) => setSearchTerm(e.target.value);

    const handleIngredientChange = (e) => {
        let value = e.target.value;
        if (!value.startsWith('#')) {
            value = `#${value}`; // Automatically prepend '#' if not present
        }
        setIngredientTerm(value);
    };

    const handleDifficultyChange = (e) => setDifficulty(e.target.value);

    const handleSearchSubmit = async () => {
        await searchRecipes(searchTerm, ingredientTerm, difficulty);
        if (searchTerm || ingredientTerm || difficulty) {
            onNewSearch(searchTerm || ingredientTerm || difficulty); // Update previous searches
        }
    };

    const searchRecipes = async (name, ingredient, difficulty) => {
        const query = new URLSearchParams();
        if (name) query.append('search', name);
        if (ingredient) query.append('ingredient', ingredient);
        if (difficulty) query.append('difficulty', difficulty);

        try {
            const response = await fetch(`http://localhost:3000/recipes?${query.toString()}`);
            if (response.ok) {
                const data = await response.json();
                onResults(data); // Pass results back to parent
            } else {
                console.error('Failed to fetch recipes:', response.statusText);
                onResults([]); // Clear results on error
            }
        } catch (error) {
            console.error('Error fetching recipes:', error);
            onResults([]); // Clear results on error
        }
    };

    return (
        <div className="search-box">
            <div className="search-input">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={handleInputChange}
                    placeholder="Search by name"
                />
                <button className="btn" onClick={handleSearchSubmit}>
                    <FontAwesomeIcon icon={faSearch} />
                </button>
            </div>
            <div className="options">
                <input
                    type="text"
                    value={ingredientTerm}
                    onChange={handleIngredientChange}
                    placeholder="Search by ingredient (use #)"
                />
                <select value={difficulty} onChange={handleDifficultyChange}>
                    <option value="">Sort by difficulty</option>
                    <option value="Easy">Easy</option>
                    <option value="Medium">Medium</option>
                    <option value="Hard">Hard</option>
                </select>
            </div>
        </div>
    );
};

export default SearchComponent;
